#include <stdio.h>                                                                                                                                                  /// เรียกใช้ไลบรารีมาตรฐานสำหรับ input และ output เช่น printf, scanf
#include <string.h>                                                                                                                                                 /// สำหรับฟังก์ชัน string เช่น strlen, strcmp

int main () {                                                                                                                                                       /// สร้างฟังก์ชันหลักเพื่อเป็นการเริ่มเขียนชุดคำสั่งเพื่อการใชเงานของ Code
    int tran,Check ;                                                                                                                                                /// สร้างตัวแปร tran เพื่อเป็นการรับค่าจำนวนธุรกรรมจากผู็ใช้ และสร้างตัวแปร Check เนื่อจากต้องนำไปเขียนฟังก์ชันในการแบ่งประเภท
    char Txn1[100],Txn5[100],Sp[5],Sc[5] ;                                                                                                                          /// สร้างตัวแปร Txn1 และ Txn5 เพื่อเป็นการรับค่าประเภทของธุรกรรมและการคิดค่าธรรมเนียมพิเศษ และสร้างตัวแปร Sp และ Sc เพื่แเป็นการเก็บข้อมมูลจำนวนของธุรกรรมของ txn1 และ txn5
    float Trva ;                                                                                                                                                    /// สร้างตัวแปร Trva เพื่อจะให้ผู้ช้สามารถกรอกข้อมมูลเกี่ยวกับมูลค่าธุรกรรมรวมของผู็ใช้ได้
    int O ,B ,A ;                                                                                                                                                   /// สร้างตัวแปร O B A เป็นจำนวนเต็มเพื่อให้สามารถนำไปเขียนเป็นประเภทของธุรกรรมโดยจำนำไปใช้ต่อใน Switch case เพื่อคำนวนค่า Nwt

    printf("\n*** Transaction Fee Calculator ***\n");                                                                                                               /// แสดงผลบนหน้าจอว่า *** Transaction Fee Calculator *** เพื่อเป็นการเตียมให้ผู็ใช้งานกรอกข้อมมูลเพื่อที่จะคำนวนยอดค่าธรรมเนียมต่างๆ

    printf("Enter number of transaction (3-12): ");                                                                                                                 /// แสดงบนหน้าจอว่า Enter number of transaction (3-12): เพื่อให้ผู็ใช้งานเตียมกรอกข้อมูลจำนวนครั้งของธุรกรรม                                                                    
    scanf("%d", &tran) ;                                                                                                                                            /// 
    printf("Enter Channel Type for TXN 1 (O,B,A): ");                                                                                                               /// แสดงบนหน้าจอว่า Enter Channel Type for TXN 1 (O,B,A): เพื่อให้ผู็ใช้งานเตียมกรอกข้อมมูล
    fgets(Txn1, sizeof(Txn1), stdin);                                                                                                                               /// รับค่าประเภทของอัตราค่าทำเนียมพื้นฐาน จากผู้ใช้เก็บใน Txn1
    sscanf(Txn1, "%s", Sp);                                                                                                                                         /// ให้ผู้ใช้งานใส่ประเภทของช่องทางุรกรรม
    
    printf("\n... (input continues) ...\n");                                                                                                                        /// แสดงผลว่า ... (input continues) ...เพื่อให้ผู้ใช้งานเห็นว่าเป็นการกรอกข้อมูลเพิ่มเติม
    printf("Enter Channel Type for TXN 5 (O,B,A): ");                                                                                                               /// แสดงบนหน้าจอว่า Enter Channel Type for TXN 5 (O,B,A): เพื่อให้ผู็ใช้งานเตียมกรอกข้อมมูล
    fgets(Txn5, sizeof(Txn5), stdin);                                                                                                                               /// รับค่าประเภทของอัตราค่าทำเนียมพื้นฐาน จากผู้ใช้เก็บใน Txn5
    sscanf(Txn5, "%s", Sc);                                                                                                                                         /// ให้ผู้ใช้งานใส่ประเภทของช่องทางุรกรรมเพื่อคิดค่าธรรมเนียมแบบพิเศษ
    printf("Enter Total Transaction Value: ");                                                                                                                      /// แสดงบนหน้าจอว่า Enter Total Transaction Value: เพื่อให้ผู้ใช้งานเตียมกรอกข้อมูลธุรกรรม                                        
    scanf("%f", &Trva);                                                                                                                                             /// ให้ผู้ใช้ใส่มูลค่าธุรกรรมเป็นทศนิยม
 
    if (Txn5[100] = O) {                                                                                                                                          
        Check = 3;                                                                                                                                                  /// หากตัวแปร txn5[100] มีค่าเป็น O ให้ Check เป็น 3
        printf("ประเภท O (ออนไลน์) ค่าธรรมเนียม: 15 บาท\n");                                                                                                           /// ให้พิมพ์มว่า ประเภท O (ออนไลน์) ค่าธรรมเนียม: 15 บาท
        if (Trva > 50000) {                                                                                                                                         /// หาก trva มากกว่า 50000
        printf("ค่าธรรมเนียมซ้อน (Compound): ท่านได้ทำธุรกรรม โดยมีจำนวนเงินมากกว่า 50000 บาท จะถูกเรียกเก็บค่าบำรุงรักษาระบบเพิ่มร้อยละ 5 จากยอดรวมค่าธรรมเนียมที่คำนวนแล้วของท่าน\n");     /// และให้พิมพ์มว่า ค่าธรรมเนียมซ้อน (Compound): ท่านได้ทำธุรกรรม โดยมีจำนวนเงินมากกว่า 50000 บาท จะถูกเรียกเก็บค่าบำรุงรักษาระบบเพิ่มร้อยละ 5 จากยอดรวมค่าธรรมเนียมที่คำนวนแล้วของท่าน                           
    } else {
        printf("์ไม่บอกเธอ\n");                                                                                                                                       /// หากไม่ ให้พิมพ์มว่า ไม่บอกเธอ
    }                                          
    } else if (Txn5[100] = B) {                                                                                                                                     
        Check = 2;                                                                                                                                                  /// หากตัวแปร txn5[100] มีค่าเป็น B ให้ Check เป็น 2
        printf("ประเภท B (ที่สาขา) ค่าธรรมเนียม: 40 บาท\n");                                                                                                            /// ให้พิมพ์มว่า ประเภท B (ที่สาขา) ค่าธรรมเนียม: 40 บาท
        printf("ช่องทาง B (ที่สาขา) คิดค่าบริการเพิ่มร้อยละ 20 จากยอดรวมค่าธรรมเนียม\n");                                                                                      /// และให้พิมพ์มว่า ช่องทาง B (ที่สาขา) คิดค่าบริการเพิ่มร้อยละ 20 จากยอดรวมค่าธรรมเนียม                                      
    } else if (Txn5[100] = A){                                                                                                                                      
        Check = 1;                                                                                                                                                  /// หากตัวแปร txn5[100] มีค่าเป็น A ให้ Check เป็น 1
        printf("ประเภท A (ตู้ ATM) ค่าธรรมเนียม: 20 บาท\n");                                                                                                            /// ให้พิมพ์มว่า ประเภท A (ตู้ ATM) ค่าธรรมเนียม: 20 บาท
        printf("ช่องทาง A (ตู้ ATM) ได้รับเครดิตคืนร้อยละ 10 จากยอดรวมค่าธรรมเนียม\n");                                                                                       /// และให้พิมพ์มว่า ช่องทาง A (ตู้ ATM) ได้รับเครดิตคืนร้อยละ 10 จากยอดรวมค่าธรรมเนียม
    } else {
        Check = 0;                                                                                                                                                  /// หากไม่ตรงกับเงื่อนไขเลย check จะมีค่าเป็น 0
    }
    
    return 0;                                                                                                                                                       /// คือค่า 0 ให้ Code เพื่อ reset กระบวนการ
    }
  